import './styles.css';
import './forossty.css';
import Navbar from '../components/Navbar';
import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import CategoryList from '../components/CategoryList';
import PdfList from '../components/PdfList';
import { ErrorContext } from '../context/ErrorContext';

function MaterialPage() {
    const [categorias, setCategorias] = useState([]);
    const [pdfs, setPdfs] = useState([]);
    const [categoriaSeleccionada, setCategoriaSeleccionada] = useState(null);
    const [isChatListOpen, setIsChatListOpen] = useState(false);
    const { showError } = useContext(ErrorContext);

    useEffect(() => {
        const fetchCategorias = async () => {
            try {
                const response = await axios.get('/api/material', { withCredentials: true });
                setCategorias(response.data.categorias);
            } catch (error) {
                showError("Error al cargar las categorías.");
                console.error("Error al cargar las categorías:", error);
            }
        };

        fetchCategorias();
    }, [showError]);

    const handleCategoriaClick = async (categoria) => {
        setCategoriaSeleccionada(categoria);
        try {
            const response = await axios.get(`/api/material/${categoria}`, { withCredentials: true });
            setPdfs(response.data.pdfs);
        } catch (error) {
            showError("Error al cargar los archivos PDF.");
            console.error("Error al cargar los archivos PDF:", error);
        }
    };

    const toggleChatList = () => {
        setIsChatListOpen((prev) => !prev);
    };

    return (
        <div>
            <Navbar name="Material" />
            <div className="container container-90vh">
                <div className="row clearfix">
                    <div className="col-lg-12">
                        <button 
                            className="btn" 
                            onClick={toggleChatList} 
                            aria-expanded={isChatListOpen}
                        >
                            {isChatListOpen ? 'Hide' : 'Show'}
                        </button>

                        <div className="card chat-app">
                            <div className={`people-list ${isChatListOpen ? 'open' : ''}`} id="plist">
                                <CategoryList 
                                    categorias={categorias} 
                                    onCategoriaClick={handleCategoriaClick} 
                                    categoriaSeleccionada={categoriaSeleccionada} 
                                />
                            </div>
                            <PdfList 
                                pdfs={pdfs} 
                                categoriaSeleccionada={categoriaSeleccionada} 
                                basePath="/PDFS" 
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default MaterialPage;
