import './styles.css';
import './forossty.css';
import Navbar from '../components/Navbar';

import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import CategoryList from '../components/CategoryList';
import PdfList from '../components/PdfList';
import { ErrorContext } from '../context/ErrorContext';

function QuizPage() {
    const [categorias, setCategorias] = useState([]);
    const [pdfs, setPdfs] = useState([]);
    const [categoriaSeleccionada, setCategoriaSeleccionada] = useState(null);
    const { showError } = useContext(ErrorContext);
    const [isCategoryListOpen, setIsCategoryListOpen] = useState(false);

    useEffect(() => {
        const fetchCategorias = async () => {
            try {
                const response = await axios.get('/api/quizes', { withCredentials: true });
                setCategorias(response.data.categorias);
            } catch (error) {
                showError("Error al cargar las categorías.");
                console.error("Error al cargar las categorías:", error);
            }
        };

        fetchCategorias();
    }, [showError]);

    const handleCategoriaClick = async (categoria) => {
        setCategoriaSeleccionada(categoria);
        try {
            const response = await axios.get(`/api/quizes/${categoria}`, { withCredentials: true });
            setPdfs(response.data.pdfs);
        } catch (error) {
            showError("Error al cargar los archivos PDF.");
            console.error("Error al cargar los archivos PDF:", error);
        }
        setIsCategoryListOpen(false); // Cierra la lista al seleccionar una categoría
    };

    const toggleCategoryList = () => {
        setIsCategoryListOpen((prev) => !prev);
    };

    return (
        <div>
            <Navbar name="Quizes" />
            <div className="container container-90vh">
                {/* Botón para alternar categorías en móviles */}
                <button
                    id="toggleChatList"
                    className="btn btn-primary d-md-none mb-3"
                    onClick={toggleCategoryList}
                >
                    {isCategoryListOpen ? 'Hide Categories' : 'Show Categories'}
                </button>

                <div className="row clearfix">
                    <div className="chat-app">
                        {/* Lista de categorías con clase condicional */}
                        <div className={`people-list ${isCategoryListOpen ? 'open' : ''}`}>
                            <CategoryList
                                categorias={categorias}
                                onCategoriaClick={handleCategoriaClick}
                                categoriaSeleccionada={categoriaSeleccionada}
                            />
                        </div>

                        {/* Lista de PDFs */}
                        <div className="chat">
                            <PdfList pdfs={pdfs} categoriaSeleccionada={categoriaSeleccionada} basePath="/QUIZZ" />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default QuizPage;
