// src/pages/QuizPage.js
import './styles.css';
import './forossty.css';
import Navbar from '../components/Navbar';
// src/pages/QuizPage.js
// src/pages/QuizPage.js
import { useParams } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CategoryList from '../components/CategoryList';
import PdfList from '../components/PdfList';

function QuizPage() {
    const { categoria } = useParams(); // Obtiene el parámetro "categoria" de la URL
    const [categorias, setCategorias] = useState([]);
    const [pdfs, setPdfs] = useState([]);
    const [error, setError] = useState(null);

    // Carga las categorías cuando se monta el componente
    useEffect(() => {
        const fetchCategorias = async () => {
            try {
                const response = await axios.get('/api/quizes', { withCredentials: true });
                setCategorias(response.data.categorias);
            } catch (error) {
                console.error("Error al cargar las categorías:", error);
                setError("Error al cargar las categorías.");
            }
        };

        fetchCategorias();
    }, []);

    // Carga los PDFs de la categoría seleccionada cuando cambia `categoria`
    useEffect(() => {
        if (categoria) {
            const fetchQuizzes = async () => {
                try {
                    const response = await axios.get(`/api/quizes/${categoria}`, { withCredentials: true });
                    setPdfs(response.data.pdfs);
                } catch (err) {
                    console.error("Error al cargar los quizzes:", err);
                    setError("Error al cargar los archivos PDF.");
                }
            };

            fetchQuizzes();
        }
    }, [categoria]);

    return (
        <div>
            <h1>Quizzes de {categoria}</h1>
            {error && <p>{error}</p>}
            <div className="container container-90vh">
                <div className="row clearfix">
                    <div className="col-lg-12">
                        <CategoryList categorias={categorias} onCategoriaClick={handleCategoriaClick} categoriaSeleccionada={categoria} />
                        <PdfList pdfs={pdfs} categoriaSeleccionada={categoria} basePath="/QUIZZ" />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default QuizPage;
